    return(
        <div class='visits-title'>
            { new Date().toLocaleDateString('fr-FR', options) } : { props.visitorsCount === 0 ? 'Aucun' : props.visitorsCount } visiteur
            { plural } pr√©sent{ plural }
        </div>
    );
}
